static int Need(void) {
  int c;
  if ((c = Greed()) != EOF) return c;
  Error(1, "unfinished expression");
}

static struct Node *Parse1(void) {
  wint_t c;
  int i, oldsp;
  struct Node *r, *p, *q;
  do {
    if ((c = Greed()) == EOF) return 0;
  } while (iswspace(c));
  if (c == L'Î»' || c == '\\') {
    oldsp = sp;
    p = r = NewNode(0, 0, 0, 0);
    ++sp;
    for (;;) {
      c = Need();
      if (c == L'Î»' || c == '\\') {
        p = p->l = NewNode(0, 0, 0, 0);
        ++sp;
        continue;
      } else {
        unget = c;
        break;
      }
    }
    q = Parse1();
    if (!q) Error(4, "lambda needs body");
    p->l = q;
    while ((q = Parse1())) {
      p->l = NewNode(2, 0, p->l, q);
    }
    sp = oldsp;
    return r;
  } else if (c == L'!') {
    // intentionally trigger undefined variable
    return NewNode(1, sp, 0, 0);
  } else if (iswdigit(c)) {
    i = 0;
    for (;;) {
      i *= 10;
      i += c - '0';
      c = Greed();
      if (c == EOF) break;
      if (!iswdigit(c)) {
        unget = c;
        break;
      }
    }
    i -= indexing;
    if (i < 0) Error(5, "undefined variable: %lc", c);
    return NewNode(1, i, 0, 0);
  } else if (c == '(') {
    p = r = Parse1();
    if (!p) Error(6, "empty parenthesis");
    while ((q = Parse1())) {
      r = NewNode(2, 0, r, q);
    }
    c = Need();
    if (c != ')') Error(7, "expected closing parenthesis");
    return r;
  } else if (c == ')') {
    unget = c;
    return 0;
  } else {
    Error(8, "unexpected character: 0x%04x %lc", c, c);
  }
}

static struct Node *Parse(void) {
  struct Node *r, *p, *q;
  p = r = Parse1();
  if (!p) Error(6, "empty expression");
  while ((q = Parse1())) {
    r = NewNode(2, 0, r, q);
  }
  return r;
}

static void Print(struct Node *p) {
  int i;
  if (p->t == 0) {
    fputc('0', stdout);
    fputc('0', stdout);
    Print(p->l);
  } else if (p->t == 1) {
    for (i = -1; i < p->x; ++i) {
      fputc('1', stdout);
    }
    fputc('0', stdout);
  } else if (p->t == 2) {
    fputc('0', stdout);
    fputc('1', stdout);
    Print(p->l);
    Print(p->r);
  } else {
    abort();
  }
}

int main(int argc, char *argv[]) {
  setlocale(LC_ALL, "");
  LoadFlags(argc, argv);
  Print(Parse());
}

