char GetBit(FILE* f) {
  wint_t c;
  char comment;
  static wint_t buf, mask;
  if (!binary) {
    for (comment = 0;;) {
      c = fgetwc(f);
      if (c == -1) break;
      if (!comment) {
        fflush(stdout);
        if (c == ';') {
          comment = 1;
        } else if (!iswspace(c) && c != '(' && c != ')' && c != '[' &&
                   c != ']') {
          if (c != -1) c &= 1;
          break;
        }
      } else if (c == '\n') {
        comment = 0;
      }
    }
  } else if (mask) {
    c = !!(buf & mask);
    mask >>= 1;
  } else {
    c = fgetc(f);
    if (c != -1) {
      buf = c;
      c = (c >> 7) & 1;
      mask = 64;
    }
  }
  return c;
}

