// finger.DescribeSyn(syn_packet_bytes:str)
//     â”œâ”€â†’ description:str
//     â””â”€â†’ nil, error:str
static int LuaDescribeSyn(lua_State *L) {
  char *q, *r;
  size_t n, m;
  const char *p;
  luaL_Buffer b;
  if (!lua_isnoneornil(L, 1)) {
    p = luaL_checklstring(L, 1, &n);
    m = 128;
    q = luaL_buffinitsize(L, &b, m);
    if ((r = DescribeSyn(q, m, p, n))) {
      luaL_pushresultsize(&b, r - q);
      return 1;
    } else {
      lua_pushnil(L);
      lua_pushstring(L, "bad syn packet");
      return 2;
    }
  } else {
    return lua_gettop(L);
  }
}

// finger.FingerSyn(syn_packet_bytes:str)
//     â”œâ”€â†’ synfinger:uint32
//     â””â”€â†’ nil, error:str
static int LuaFingerSyn(lua_State *L) {
  size_t n;
  uint32_t x;
  const char *p;
  if (!lua_isnoneornil(L, 1)) {
    p = luaL_checklstring(L, 1, &n);
    if ((x = FingerSyn(p, n))) {
      lua_pushinteger(L, x);
      return 1;
    } else {
      lua_pushnil(L);
      lua_pushstring(L, "bad syn packet");
      return 2;
    }
  } else {
    return lua_gettop(L);
  }
}

// finger.GetSynFingerOs(synfinger:uint32)
