    if (fstat(fd, &st) == -1) {
      close(fd);
      return LuaUnixSysretErrno(L, "fstat", olderr);
    }
    if (i < 0) {
      i = st.st_size + (i + 1);
    }
    if (j < 0) {
      j = st.st_size + (j + 1);
    }
