                    break;
                  case 'S': /* \eOS is F4 */
                    pf4_ = !pf4_;
                    break;
                  case 'T': /* \eOT is F5 */
                    pf5_ = !pf5_;
                    break;
                  case 'U': /* \eOU is F6 */
                    pf6_ = !pf6_;
                    break;
