int column_;

wontreturn void usage(int rc, FILE *f) {
  fputs("Usage: ", f);
  fputs(program_invocation_name, f);
  fputs(" [-w COLS] [FILE...]\n", f);
  exit(rc);
}

void getopts(int argc, char *argv[]) {
  int opt;
  while ((opt = getopt(argc, argv, "?hw:")) != -1) {
    switch (opt) {
      case 'w':
        column_ = strtol(optarg, NULL, 0);
        break;
      case '?':
      case 'h':
        usage(EXIT_SUCCESS, stdout);
      default:
        usage(EX_USAGE, stderr);
    }
  }
  if (column_ <= 0) {
    column_ = 64;
  }
}

void fold(FILE *fin, FILE *fout) {
  unsigned column = 0;
  for (;;) {
    wint_t wc = fgetwc(fin);
    if (wc != -1) {
      fputwc(wc, fout);
      if (wc == '\n') {
        column = 0;
      } else if (++column == column_) {
        fputc('\n', fout);
        column = 0;
      }
    } else {
      if (feof(fin)) {
        return;
      } else {
        fprintf(stderr, "%s: %d\n", "fgetwc", errno);
        exit(1);
      }
    }
  }
}

int main(int argc, char *argv[]) {
  getopts(argc, argv);
  fold(stdin, stdout);
  return 0;
}

