int ispipe_;
int newlines_;

wontreturn void ShowUsage(FILE *f, int rc) {
  fputs(program_invocation_name, f);
  fputs(": [-p] [-n] [FILE...]\n", f);
  exit(rc);
}

void GetOpts(int argc, char *argv[]) {
  int opt;
  while ((opt = getopt(argc, argv, "hpn")) != -1) {
    switch (opt) {
      case 'p':
        ispipe_ = true;
        break;
      case 'n':
        newlines_ = true;
        break;
      case 'h':
        ShowUsage(stdout, EXIT_SUCCESS);
      default:
        ShowUsage(stderr, EX_USAGE);
    }
  }
}

void bing(FILE *f) {
  int c, c2;
  while ((c = fgetc(f)) != -1) {
    c2 = c == '\n' && newlines_ ? '\n' : kCp437[c & 0xff];
    if (ispipe_) {
      fputc(c, stdout);
      fputwc(c2, stderr);
    } else {
      fputwc(c2, stdout);
    }
  }
  fputc('\n', ispipe_ ? stderr : stdout);
}

int main(int argc, char *argv[]) {
  FILE *f;
  size_t i;
  GetOpts(argc, argv);
  if (optind < argc) {
    for (i = optind; i < argc; ++i) {
      if (!(f = fopen(argv[i], "rb"))) {
        perror(argv[i]);
        exit(EXIT_FAILURE);
      }
      bing(f);
      fclose(f);
    }
  } else {
    bing(stdin);
  }
  return 0;
}

