static void *ConvertFloatToDoubleMatrix(long yn, long xn, double Md[yn][xn],
                                        const float Mf[yn][xn]) {
  long y, x;
  for (y = 0; y < yn; ++y) {
    for (x = 0; x < xn; ++x) {
      Md[y][x] = Mf[y][x];
    }
  }
  return Md;
}

