void *ConvertMatrixToStringTable(long yn, long xn, char *T[yn][xn],
                                 const double M[yn][xn], double digs,
                                 double rounder(double)) {
  long y, x;
  assert(yn && xn && !T[0][0]);
  for (y = 0; y < yn; ++y) {
    for (x = 0; x < xn; ++x) {
      T[y][x] = xmalloc(40);
      T[y][x][0] = '\0';
      g_dfmt_p(T[y][x], (void *)&M[y][x], digs, 40, 0);
    }
  }
  return T;
}

