#include "libc/log/check.h"
#include "tool/viz/lib/halfblit.h"

void *halfblit(size_t n, void *block) {
  unsigned y, x;
  char(*dst)[n << 0][n << 0] = block;
  char(*src)[n << 1][n << 1] = block;
  DCHECK_GE(n, 2);
  DCHECK_ALIGNED(16, block);
  for (y = 0; y < n; ++y) {
    for (x = 0; x < n; ++x) {
      (*dst)[y][x] = (*src)[y][x];
    }
  }
  return block;
}

