  y = Cdr(y);  // ((Î» ğ‘¥ ğ‘¦) ğ‘)
  u = Cdr(y);  //          ğ‘
  y = Car(y);  //  (Î» ğ‘¥ ğ‘¦)
  p = Car(Cdr(y));
  b = Car(Cdr(Cdr(y)));
