/**
 * Matches
 *
 *     (Î» V (F . V) . Q)
 *
 * @return F on success, or 0 on mismatch
 * @note Q is ignored
 * @note V must be a non-nil atom
 * @note Î» means LAMBDA
 */
int IsDelegate(int x_) {
  dword w_;
  if (x_ >= 0) return 0;
  w_ = Get(x_);  // (Î» V (F . V) . Q)
  int ax_ = LO(w_);
  int dx_ = HI(w_);
  if (ax_ != kLambda) return 0;
  if (dx_ >= 0) return 0;
  w_ = Get(dx_);  // (V (F . V) . Q)
  int adx_ = LO(w_);
  int ddx_ = HI(w_);
  int V = adx_;
  if (V <= 0) return 0;
  if (ddx_ >= 0) return 0;
  w_ = Get(ddx_);  // ((F . V) . Q)
  int addx_ = LO(w_);
  if (addx_ >= 0) return 0;
  w_ = Get(addx_);  // (F . V)
  int aaddx_ = LO(w_);
  int daddx_ = HI(w_);
  int F = aaddx_;
  if (daddx_ != V) return 0;
  return F;
}

