void PlanFuncalls(int n, dword p, int x) {
  int h;
  if (x < 0 && (h = Car(x)) != kQuote && h != kClosure && h != kMacro) {
    if (h == kLambda) {
      if (!HasAtom(n, Cadr(x))) {
        PlanFuncalls(n, p, Caddr(x));
      }
    } else if (h == kCond) {
      while ((x = Cdr(x)) < 0) {
        if ((h = Car(x)) < 0) {
          PlanFuncalls(n, p, Car(h));
          if ((h = Cdr(h)) < 0) {
            PlanFuncalls(n, p, Car(h));
          }
        }
      }
    } else {
      if (h == n) {
        SetShadow(x, p);
      }
      while (x) {
        if (x < 0) {
          h = Car(x);
          x = Cdr(x);
        } else {
          h = x;
          x = 0;
        }
        PlanFuncalls(n, p, h);
      }
    }
  }
}

