int PrintChar(int fd, int s) {
  unsigned c;
  int d, e, i, n;
  c = s & 0xffffffff;
  if (bp[fd] + 6 > sizeof(g_buffer[fd])) Flush(fd);
  if (c < 0200) {
    g_buffer[fd][bp[fd]++] = c;
    if (c == L'\n') Flush(fd);
  } else {
    d = c;
    e = kTpEnc[bsrl(d) - 7];
    i = n = e & 255;
    do g_buffer[fd][bp[fd] + i--] = 0200 | (d & 077);
    while (d >>= 6, i);
    g_buffer[fd][bp[fd]] = d | e >> 8;
    bp[fd] += n + 1;
  }
  return GetMonospaceCharacterWidth(c);
}

