  long ssusr1;      /* Reserved for user.  */
  long ssusr2;      /* Reserved for user.  */
  long sstpid;      /* Process ID for pid based multi-tasking.  */
  long ssgvup;      /* Pointer to multitasking thread giveup.  */
  long sscray[7];   /* Reserved for Cray Research.  */
  long ssa0;
  long ssa1;
  long ssa2;
  long ssa3;
  long ssa4;
  long ssa5;
  long ssa6;
  long ssa7;
  long sss0;
  long sss1;
  long sss2;
  long sss3;
  long sss4;
